INSERT INTO agents (name, role, skills, dica, biography, imageKey, abilities, strategies, tutorials) VALUES ('Jett', 'Duelist', 'Movimentos Ágeis', 'Domine as habilidades de movimento', 'Representando a Coreia do Sul, sua terra natal, Jett tem um estilo de luta ágil e evasivo que permite que ela assuma riscos como ninguém. Ela corre em meio a qualquer confronto, cortando os inimigos antes mesmo que eles percebam quem ou o que os atingiu.', 'jett-card', '[{"name":"Corrente Ascendente","icon":"fas fa-arrow-up","cost":150,"charges":1},{"name":"Brisa de Impulso","icon":"fas fa-wind","cost":0,"charges":1},{"name":"Erupção de Brumas","icon":"fas fa-fan","cost":200,"charges":2},{"name":"Tempestade de Lâminas","icon":"fas fa-khanda","cost":8,"charges":1,"isUltimate":true}]', '[{"title":"Entrada Agressiva","description":"Use a mobilidade de Jett para entrar rapidamente em sites e criar espaço para sua equipe.","tips":["Combine Brisa de Impulso com Corrente Ascendente para movimentos imprevisíveis","Use Erupção de Brumas para limpar ângulos comuns","Mantenha uma rota de fuga planejada"]},{"title":"Operador Expert","description":"Jett é excelente com o Operador devido à sua mobilidade após atirar.","tips":["Posicione-se em ângulos arriscados","Após o tiro, use Brisa de Impulso para recuar","Alterne entre posições agressivas e passivas"]}]', '[{"title":"Dominando a Mobilidade","description":"Aprenda as melhores combinações de habilidades para movimento.","thumbnail":"jettMobility"},{"title":"Guia do Operator","description":"Como usar a Operator efetivamente com Jett.","thumbnail":"jettOperator"}]');
INSERT INTO agents (name, role, skills, dica, biography, imageKey, abilities, strategies, tutorials) VALUES ('Sova', 'Initiator', 'Reconhecimento', 'Use as flechas de reconhecimento eficientemente', 'Nascido sob o eterno inverno das tundras russas, Sova rastreia, encontra e elimina inimigos com eficiência e precisão implacáveis. Seu arco personalizado e suas habilidades de reconhecimento garantem que nenhuma presa escape.', 'sova-card', '[{"name":"Flecha Chocante","icon":"fas fa-bolt","cost":150,"charges":2},{"name":"Drone","icon":"fas fa-dove","cost":400,"charges":1},{"name":"Flecha Rastreadora","icon":"fas fa-eye","cost":150,"charges":2},{"name":"Fúria do Caçador","icon":"fas fa-crosshairs","cost":8,"charges":1,"isUltimate":true}]', '[{"title":"Reconhecimento Efetivo","description":"Use as flechas de Sova para coletar informações cruciais para sua equipe.","tips":["Aprenda alinhamentos de flechas comuns","Use o drone para entradas seguras","Comunique as informações para sua equipe"]},{"title":"Pós-Planta Mestre","description":"Sova é excelente em situações pós-planta com suas flechas e ultimate.","tips":["Aprenda posições seguras para usar flechas","Combine ultimate com informações da equipe","Use flechas para negar defuse"]}]', '[{"title":"Alinhamentos Essenciais","description":"Os principais alinhamentos de flechas para cada mapa.","thumbnail":"sovaSpots"},{"title":"Guia de Reconhecimento","description":"Como maximizar a coleta de informações.","thumbnail":"sovaDrone"}]');
INSERT INTO agents (name, role, skills, dica, biography, imageKey, abilities, strategies, tutorials) VALUES ('Omen', 'Controller', 'Manipulação', 'Posicionamento estratégico de smokes e teleportes', 'Omen, um assassino misterioso e habilidoso, usa os poderes de invocação de um antigo artefato para manipular a realidade e caçar seus inimigos. Ele pode aparecer e desaparecer em um piscar de olhos, deixando seus oponentes confusos e desorientados.', 'omen-card', '[{"name":"Paranóia","icon":"fas fa-eye-slash","cost":100,"charges":1},{"name":"Manto Sombrio","icon":"fas fa-location-arrow","cost":100,"charges":1},{"name":"Passos Tenebrosos","icon":"fas fa-eye-slash","cost":100,"charges":1},{"name":"Ultimate","icon":"fas fa-eye-slash","cost":100,"charges":1,"isUltimate":true}]', '[{"title":"Controle de Inimigos","description":"Use a invisibilidade de Omen para se mover sem ser visto e atacar inimigos.","tips":["Use a invisibilidade para se mover sem ser visto","Use a Ultimate para controlar inimigos e surpreender","Use a paranóia para cegar os inimigos e atacar"]}]', '[{"title":"Controle de Inimigos","description":"Use a invisibilidade de Omen para se mover sem ser visto e atacar inimigos.","thumbnail":"/assets/tutorials/omen-control.jpg"}]');
