INSERT INTO agents (name, role, skills, dica, biography, imageKey, abilities, strategies, tutorials) VALUES ('Killjoy', 'Sentinel', 'Tecnologia', 'Use suas torres e armadilhas para controlar o mapa.', 'Killjoy, uma engenheira alemã brilhante, utiliza invenções de ponta para proteger zonas e obter vantagem estratégica sobre seus inimigos.', 'killjoy-card', '[{"name":"Nanoenxame","icon":"fas fa-bolt","cost":200,"charges":2},{"name":"Robô de Alarme","icon":"fas fa-bolt","cost":200,"charges":1},{"name":"Torre","icon":"fas fa-bolt","cost":0,"charges":1},{"name":"Confinamento","icon":"fas fa-bolt","cost":7,"charges":1,"isUltimate":true}]', '[{"title":"Controle de Rotas","description":"Use o Robô de Alarme e a Torre para travar acessos importantes.","tips":["Posicione a Torre em locais elevados para melhor cobertura.","Combine Nanoenxame com o Fio de Alarme para dano garantido.","Use Confinamento em retakes ou ataques finais."]}]', '[{"title":"Armadilhas Inteligentes","description":"Aprenda a posicionar a Torre e o Nanoenxame de forma estratégica.","thumbnail":"/assets/tutorials/killjoy-traps.jpg"}]');
INSERT INTO agents (name, role, skills, dica, biography, imageKey, abilities, strategies, tutorials) VALUES ('Brimstone', 'Controller', 'Suporte tático', 'Use os smokes e ataques orbitais para isolar e dominar áreas.', 'Brimstone, um comandante americano, lidera o combate com precisão cirúrgica, usando seus recursos para apoiar sua equipe em campo.', 'brimstone-card', '[{"name":"Incendiário","icon":"fas fa-bolt","cost":250,"charges":1},{"name":"Sinalizador Estimulante","icon":"fas fa-bolt","cost":200,"charges":2},{"name":"Fumaça Celeste","icon":"fas fa-bolt","cost":100,"charges":3},{"name":"Ataque Orbital","icon":"fas fa-bolt","cost":6,"charges":1,"isUltimate":true}]', '[{"title":"Domínio de Área","description":"Crie zonas de negação com smokes e incendiários.","tips":["Use Fumaça Celeste para bloquear visão antes de iniciar a entrada.","Combine Incendiário com info da equipe para impedir defuses.","Utilize Ataque Orbital em locais plantados ou para forçar saídas inimigas."]}]', '[{"title":"Smokes Eficientes","description":"Aprenda como usar suas smokes para cortar visão inimiga.","thumbnail":"/assets/tutorials/brimstone-smokes.jpg"}]');
INSERT INTO agents (name, role, skills, dica, biography, imageKey, abilities, strategies, tutorials) VALUES ('Sage', 'Sentinel', 'Cura e suporte', 'Suporte sua equipe com cura e controle de movimentação inimiga.', 'Sage é uma monja chinesa com habilidades curativas e defensivas que garantem estabilidade para sua equipe nas piores situações.', 'sage-card', '[{"name":"Orbe de Lentidão","icon":"fas fa-bolt","cost":200,"charges":2},{"name":"Orbe de Cura","icon":"fas fa-bolt","cost":0,"charges":1},{"name":"Orbe de Barreira","icon":"fas fa-bolt","cost":400,"charges":1},{"name":"Ressurreição","icon":"fas fa-bolt","cost":8,"charges":1,"isUltimate":true}]', '[{"title":"Retenção e Cura","description":"Use a barreira para bloquear e o orbe para curar aliados.","tips":["Bloqueie corredores e entradas com o Orbe de Barreira.","Mantenha-se viva para poder usar a Ressurreição em momentos chave.","Orbe de Lentidão funciona bem em locais de rush ou planta/desarme."]}]', '[{"title":"Sustentação da Equipe","description":"Aprenda a ser o suporte essencial com Sage.","thumbnail":"/assets/tutorials/sage-support.jpg"}]');
INSERT INTO agents (name, role, skills, dica, biography, imageKey, abilities, strategies, tutorials) VALUES ('Breach', 'Initiator', 'Controle de território', 'Cegue, atordoe e force os inimigos a sair da posição.', 'Breach, um sueco com implantes biomecânicos, gera tremores de impacto que desestruturam qualquer defesa inimiga.', 'breach-card', '[{"name":"Ponto de Ignição","icon":"fas fa-bolt","cost":250,"charges":2},{"name":"Pós-choque","icon":"fas fa-bolt","cost":200,"charges":1},{"name":"Falha","icon":"fas fa-bolt","cost":200,"charges":1},{"name":"Onda Sísmica","icon":"fas fa-bolt","cost":7,"charges":1,"isUltimate":true}]', '[{"title":"Abertura Forçada","description":"Prepare o push da sua equipe desestruturando a defesa inimiga.","tips":["Use Ponto de Ignição antes da entrada para cegar inimigos.","Combine Pós-choque com info de aliados para tirar inimigos do pixel.","A ultimate pode limpar todo um bombsite sozinha."]}]', '[{"title":"Iniciações Perfeitas","description":"Aprenda os combos ideais para abrir espaço com Breach.","thumbnail":"/assets/tutorials/breach-init.jpg"}]');
INSERT INTO agents (name, role, skills, dica, biography, imageKey, abilities, strategies, tutorials) VALUES ('Cypher', 'Sentinel', 'Informação e armadilhas', 'Controle áreas com informações e antecipe movimentos do inimigo.', 'Cypher, o espião marroquino, é mestre em vigiar e rastrear seus inimigos sem ser notado.', 'cypher-card', '[{"name":"Fio-armadilha","icon":"fas fa-bolt","cost":200,"charges":2},{"name":"Jaula Cibernética","icon":"fas fa-bolt","cost":100,"charges":2},{"name":"Câmera Espiã","icon":"fas fa-bolt","cost":0,"charges":1},{"name":"Assalto Neural","icon":"fas fa-bolt","cost":6,"charges":1,"isUltimate":true}]', '[{"title":"Vigilância Total","description":"Ganhe informação precisa com armadilhas e câmeras bem posicionadas.","tips":["Coloque fios em locais improváveis para surpreender inimigos.","Use a jaula com os fios para combos de controle e abate.","Use Assalto Neural logo após um abate para revelar toda a equipe inimiga."]}]', '[{"title":"Setup de Defesa","description":"Configure armadilhas e câmera para segurar bombsites.","thumbnail":"/assets/tutorials/cypher-setup.jpg"}]');
INSERT INTO agents (name, role, skills, dica, biography, imageKey, abilities, strategies, tutorials) VALUES ('Chamber', 'Sentinel', 'Precisão e armadilhas', 'Use armadilhas para informação e elimine com precisão cirúrgica.', 'Um designer de armas francês extremamente confiante, Chamber defende o campo com estilo e precisão letal.', 'chamber-card', '[{"name":"Marca Registrada","icon":"fas fa-bolt","cost":200,"charges":1},{"name":"Caçador de Cabeças","icon":"fas fa-bolt","cost":150,"charges":8},{"name":"Rendezvous","icon":"fas fa-bolt","cost":0,"charges":1},{"name":"Tour de Force","icon":"fas fa-bolt","cost":7,"charges":1,"isUltimate":true}]', '[{"title":"Controle e Sniper","description":"Utilize o teleport para jogar avançado e reposicionar rapidamente.","tips":["Use a Marca Registrada para avisar entradas.","Equipe o Caçador de Cabeças para duelos curtos.","Tour de Force é ideal para segurar bomb sozinho com precisão."]}]', '[{"title":"Defesa Avançada","description":"Como dominar a defesa com teleport e precisão.","thumbnail":"/assets/tutorials/chamber-sniper.jpg"}]');
INSERT INTO agents (name, role, skills, dica, biography, imageKey, abilities, strategies, tutorials) VALUES ('Fade', 'Initiator', 'Informação e medo', 'Use sua ultimate para revelar e sufocar os oponentes.', 'Fade, uma caçadora de pesadelos da Turquia, assombra seus inimigos com visões e gritos aterrorizantes.', 'fade-card', '[{"name":"Assombrar","icon":"fas fa-bolt","cost":200,"charges":1},{"name":"Apreensão","icon":"fas fa-bolt","cost":200,"charges":1},{"name":"Garras","icon":"fas fa-bolt","cost":250,"charges":2},{"name":"Abraço da Noite","icon":"fas fa-bolt","cost":7,"charges":1,"isUltimate":true}]', '[{"title":"Caça Coordenada","description":"Combine info com entrada rápida do time.","tips":["Jogue Assombrar por cima de paredes para revelar grupos.","Apreensão prende e impede recuos.","Ultimate ideal em execuções de site."]}]', '[{"title":"Reveal Total","description":"Lineups de Fade para diferentes mapas.","thumbnail":"/assets/tutorials/fade-reveal.jpg"}]');
INSERT INTO agents (name, role, skills, dica, biography, imageKey, abilities, strategies, tutorials) VALUES ('Neon', 'Duelist', 'Velocidade e agressão', 'Use sua velocidade para abrir espaço e confundir o inimigo.', 'Neon, uma agente filipina, corre mais rápido que a luz e choca os inimigos com pura energia.', 'neon-card', '[{"name":"Ricochete Elétrico","icon":"fas fa-bolt","cost":200,"charges":1},{"name":"Mergulho Elétrico","icon":"fas fa-bolt","cost":150,"charges":1},{"name":"Velocidade","icon":"fas fa-bolt","cost":0,"charges":1},{"name":"Alta Voltagem","icon":"fas fa-bolt","cost":7,"charges":1,"isUltimate":true}]', '[{"title":"Corrida Rápida","description":"Corra e elimine rapidamente antes que o inimigo reaja.","tips":["Use a parede dupla da Velocidade para entrar com segurança.","Combine Ricochete com rush para confundir.","A ultimate é letal em combate próximo."]}]', '[{"title":"Entradas Instantâneas","description":"Como surpreender inimigos com movimentação explosiva.","thumbnail":"/assets/tutorials/neon-speed.jpg"}]');
INSERT INTO agents (name, role, skills, dica, biography, imageKey, abilities, strategies, tutorials) VALUES ('Skye', 'Initiator', 'Cura e informação', 'Use seus bichos para explorar e atrapalhar a defesa inimiga.', 'Vinda da Austrália, Skye comanda a natureza para curar aliados e rastrear inimigos com seus animais espirituais.', 'skye-card', '[{"name":"Reflorescer","icon":"fas fa-bolt","cost":150,"charges":2},{"name":"Luz Guia","icon":"fas fa-bolt","cost":250,"charges":2},{"name":"Predador Explosivo","icon":"fas fa-bolt","cost":300,"charges":1},{"name":"Rastreadores","icon":"fas fa-bolt","cost":7,"charges":1,"isUltimate":true}]', '[{"title":"Reconhecimento Natural","description":"Use os animais para avançar com info segura.","tips":["Predador Explosivo limpa cantos perigosos.","Cure aliados antes de entradas importantes.","Use Rastreadores para rastrear inimigos escondidos."]}]', '[{"title":"Skye Iniciadora","description":"Rotinas completas para iniciar com flash e cura.","thumbnail":"/assets/tutorials/skye-init.jpg"}]');
INSERT INTO agents (name, role, skills, dica, biography, imageKey, abilities, strategies, tutorials) VALUES ('Viper', 'Controller', 'Controle com veneno', 'Crie zonas tóxicas para impedir movimentação e plantar com segurança.', 'Vinda dos EUA, Viper usa produtos químicos letais para controlar áreas inteiras com fumaça e ácido.', 'viper-card', '[{"name":"Nuvem Venenosa","icon":"fas fa-bolt","cost":200,"charges":1},{"name":"Veneno de Cobra","icon":"fas fa-bolt","cost":100,"charges":2},{"name":"Cortina Tóxica","icon":"fas fa-bolt","cost":0,"charges":1},{"name":"Poço Peçonhento","icon":"fas fa-bolt","cost":8,"charges":1,"isUltimate":true}]', '[{"title":"Domínio Tóxico","description":"Corte visão e force reposições com paredes venenosas.","tips":["Combine Veneno de Cobra com barreiras para dano garantido.","Nuvem pode ser reposicionada a cada rodada.","Poço Peçonhento é excelente para segurar spike plantada."]}]', '[{"title":"Viper Lineups","description":"Melhores posicionamentos para plantas e defuses.","thumbnail":"/assets/tutorials/viper-lineups.jpg"}]');
INSERT INTO agents (name, role, skills, dica, biography, imageKey, abilities, strategies, tutorials) VALUES ('KAY/O', 'Initiator', 'Supressão', 'Anule as habilidades inimigas e abra entradas com segurança.', 'KAY/O é uma máquina de guerra construída para neutralizar Radiantes. Ele utiliza pulsos de supressão e granadas para dominar o campo.', 'kayo-card', '[{"name":"FRAG/mento","icon":"fas fa-bolt","cost":200,"charges":1},{"name":"FLASH/drive","icon":"fas fa-bolt","cost":250,"charges":2},{"name":"ZERO/ponto","icon":"fas fa-bolt","cost":0,"charges":1},{"name":"NULL/cmd","icon":"fas fa-bolt","cost":7,"charges":1,"isUltimate":true}]', '[{"title":"Supressão Tática","description":"Neutralize inimigos antes da entrada.","tips":["Use ZERO/ponto em áreas comuns de ângulo.","Combine FLASH/drive com rush rápido.","NULL/cmd permite entrar e suprimir tudo ao redor."]}]', '[{"title":"Push Antihabilidade","description":"Como tirar poder dos Radiantes com KAY/O.","thumbnail":"/assets/tutorials/kayo-suppress.jpg"}]');
INSERT INTO agents (name, role, skills, dica, biography, imageKey, abilities, strategies, tutorials) VALUES ('Astra', 'Controller', 'Controle global', 'Use a visão astral para bloquear, prender e desacelerar inimigos.', 'Astra é uma agente de Gana com a habilidade de manipular o campo de batalha em escala cósmica.', 'astra-card', '[{"name":"Poço Gravitacional","icon":"fas fa-bolt","cost":150,"charges":2},{"name":"Pulso Nova","icon":"fas fa-bolt","cost":150,"charges":2},{"name":"Nebulosa","icon":"fas fa-bolt","cost":150,"charges":2},{"name":"Forma Astral / Divisor Cósmico","icon":"fas fa-bolt","cost":7,"charges":1,"isUltimate":true}]', '[{"title":"Controle à Distância","description":"Planeje e reaja com precisão global.","tips":["Use estrelas no início da rodada para setups completos.","Combine Poço com entry da sua equipe.","O ultimate corta toda visão e som."]}]', '[{"title":"Jogando na Visão Astral","description":"Controle completo do mapa com Astra.","thumbnail":"/assets/tutorials/astra-control.jpg"}]');
INSERT INTO agents (name, role, skills, dica, biography, imageKey, abilities, strategies, tutorials) VALUES ('Yoru', 'Duelist', 'Engano e infiltração', 'Use sua movimentação para confundir e pegar inimigos de surpresa.', 'Yoru é um infiltrador japonês que manipula o espaço dimensional para enganar e surpreender.', 'yoru-card', '[{"name":"Finta","icon":"fas fa-bolt","cost":100,"charges":2},{"name":"Invasão Dimensional","icon":"fas fa-bolt","cost":0,"charges":1},{"name":"Passagem","icon":"fas fa-bolt","cost":200,"charges":1},{"name":"Espionagem Dimensional","icon":"fas fa-bolt","cost":7,"charges":1,"isUltimate":true}]', '[{"title":"Engane e Elimine","description":"Crie confusão e capitalize nos flancos.","tips":["Use Finta para simular entradas.","Passagem pode te reposicionar atrás dos inimigos.","Use a Ultimate para infiltração total."]}]', '[{"title":"Yoru Avançado","description":"Domine os truques e mind games.","thumbnail":"/assets/tutorials/yoru-fakes.jpg"}]');
INSERT INTO agents (name, role, skills, dica, biography, imageKey, abilities, strategies, tutorials) VALUES ('Harbor', 'Controller', 'Água e bloqueio', 'Use barreiras e marés para proteger sua equipe.', 'Harbor, um guerreiro indiano, manipula a água com tecnologia ancestral para controlar o campo de batalha.', 'harbor-card', '[{"name":"Maré Alta","icon":"fas fa-bolt","cost":0,"charges":1},{"name":"Enseada","icon":"fas fa-bolt","cost":150,"charges":2},{"name":"Cascata","icon":"fas fa-bolt","cost":200,"charges":1},{"name":"Dilúvio","icon":"fas fa-bolt","cost":7,"charges":1,"isUltimate":true}]', '[{"title":"Controle Fluido","description":"Use paredes móveis para entry e retake.","tips":["Use Cascata para cobertura rápida.","Dilúvio marca inimigos e reduz mobilidade.","Combine Maré Alta com entrada sincronizada."]}]', '[{"title":"Paredes de Água","description":"Rotas e bloqueios com Harbor.","thumbnail":"/assets/tutorials/harbor-walls.jpg"}]');
