INSERT INTO agents (name, role, skills, dica, biography, imageKey, abilities, strategies, tutorials) VALUES ('Deadlock', 'Sentinel', 'Controle com armadilhas sonoras', 'Trave o avanço inimigo com precisão e armadilhas.', 'Deadlock é uma especialista norueguesa em contenção, com ferramentas que punem movimentações bruscas.', 'deadlock-card', '[{"name":"Barreira Sônica","icon":"fas fa-bolt","cost":200,"charges":1},{"name":"Sensor Sônico","icon":"fas fa-bolt","cost":200,"charges":1},{"name":"Teia","icon":"fas fa-bolt","cost":150,"charges":2},{"name":"Confinamento Letal","icon":"fas fa-bolt","cost":7,"charges":1,"isUltimate":true}]', '[{"title":"Contenção Total","description":"Forçe movimentação ou puna com armadilhas.","tips":["Teia é ideal para entradas rápidas.","Sensor Sônico revela inimigos barulhentos.","Confinamento pode neutralizar posições seguras."]}]', '[{"title":"Deadlock Defensiva","description":"Como posicionar cada armadilha com eficiência.","thumbnail":"/assets/tutorials/deadlock-def.jpg"}]');
INSERT INTO agents (name, role, skills, dica, biography, imageKey, abilities, strategies, tutorials) VALUES ('Clove', 'Controller', 'Revivência e agressividade', 'Use a segunda chance de vida para punir os inimigos.', 'Clove, vinda da Escócia, é uma controladora ousada com a habilidade de retornar à vida temporariamente.', 'clove-card', '[{"name":"Bruma","icon":"fas fa-bolt","cost":150,"charges":2},{"name":"Vigor","icon":"fas fa-bolt","cost":250,"charges":1},{"name":"Enfraquecimento","icon":"fas fa-bolt","cost":200,"charges":1},{"name":"Segunda Chance","icon":"fas fa-bolt","cost":7,"charges":1,"isUltimate":true}]', '[{"title":"Vida Dupla","description":"Seja agressivo sabendo que pode voltar à luta.","tips":["Use Bruma para cobrir avanço.","Vigor oferece vida extra no clutch.","Ultimate te dá uma nova oportunidade no 1vX."]}]', '[{"title":"Retorno à Vida","description":"Como abusar da Ultimate e pegar de surpresa.","thumbnail":"/assets/tutorials/clove-revive.jpg"}]');
INSERT INTO agents (name, role, skills, dica, biography, imageKey, abilities, strategies, tutorials) VALUES ('Iso', 'Duelist', 'Duelos 1v1', 'Procure duelos isolados para ativar sua ultimate com eficácia.', 'Iso, um mercenário chinês, manipula energia para enfrentar inimigos em duelos isolados e letais.', 'iso-card', '[{"name":"Reflexão","icon":"fas fa-bolt","cost":200,"charges":1},{"name":"Muralha de Energia","icon":"fas fa-bolt","cost":150,"charges":1},{"name":"Pulso de Disrupção","icon":"fas fa-bolt","cost":250,"charges":1},{"name":"Zona de Confronto","icon":"fas fa-bolt","cost":7,"charges":1,"isUltimate":true}]', '[{"title":"Confronto Pessoal","description":"Elimine alvos isolados com vantagem.","tips":["Muralha de Energia pode bloquear visão e som.","Pulso é ótimo para limpar cantos.","Ultimate força duelo — esteja pronto."]}]', '[{"title":"1v1 Mortal","description":"Como dominar duelos com Iso.","thumbnail":"/assets/tutorials/iso-1v1.jpg"}]');
INSERT INTO agents (name, role, skills, dica, biography, imageKey, abilities, strategies, tutorials) VALUES ('Wayley', 'Duelist', 'Som e manipulação', 'Use suas ondas sonoras para negar visão e dividir espaços.', 'Way é uma agente com sensibilidades auditivas aprimoradas, capaz de controlar o campo com vibrações sonoras e criar barreiras acústicas que confundem seus inimigos.', 'wayley-card', '[{"name":"Eco Pulsante","icon":"fas fa-bolt","cost":200,"charges":2},{"name":"Barreira Sônica","icon":"fas fa-bolt","cost":150,"charges":1},{"name":"Fusão Acústica","icon":"fas fa-bolt","cost":250,"charges":1},{"name":"Campos Vibrantes","icon":"fas fa-bolt","cost":7,"charges":1,"isUltimate":true}]', '[{"title":"Controle Acústico","description":"Projete som para controlar entradas e bloquear ângulos críticos.","tips":["Use Eco Pulsante para rastrear movimentações por áudio.","Barreira Sônica impede visão e dificulta defuse.","Ultimate pressuriza espaço sonoro e prejudica audição inimiga."]}]', '[{"title":"Sam controle de som","description":"Como usar ondas sonoras para dominar áreas.","thumbnail":"/assets/tutorials/way-soundcontrol.jpg"}]');
INSERT INTO agents (name, role, skills, dica, biography, imageKey, abilities, strategies, tutorials) VALUES ('Raze', 'Duelist', 'Explosivos e mobilidade', 'Use explosivos para abrir espaço e eliminar múltiplos inimigos.', 'Raze, vinda do Brasil, é especialista em demolição e adora causar caos no campo de batalha. Com seu arsenal de explosivos, ela é perfeita para abrir espaço e garantir eliminações rápidas.', 'raze-card', '[{"name":"Carga de Explosivos","icon":"fas fa-bomb","cost":200,"charges":2},{"name":"Granada de Fragmentação","icon":"fas fa-bomb","cost":250,"charges":1},{"name":"Bumba","icon":"fas fa-robot","cost":300,"charges":1},{"name":"Estrondo Final","icon":"fas fa-rocket","cost":8,"charges":1,"isUltimate":true}]', '[{"title":"Entrada Explosiva","description":"Use suas habilidades para forçar inimigos a sair de posições seguras.","tips":["Combine Carga de Explosivos com movimentação agressiva.","Use a Granada de Fragmentação para limpar cantos.","O Bumba pode ser usado para obter informação e pressionar inimigos."]},{"title":"Ultimate Impactante","description":"O Estrondo Final pode garantir eliminações múltiplas em situações de clutch ou retake.","tips":["Use o ultimate em áreas fechadas para maximizar o dano.","Combine com informações da equipe para pegar inimigos desprevenidos.","Não tenha medo de usar o Estrondo Final para abrir espaço em rounds decisivos."]}]', '[{"title":"Combos Explosivos","description":"Aprenda a combinar Carga de Explosivos com Granada de Fragmentação para eliminações rápidas.","thumbnail":"razeCombo"},{"title":"Domínio do Bumba","description":"Como usar o Bumba para obter informação e pressionar inimigos.","thumbnail":"razeBumba"}]');
